$(".model__tab").click(function () {
    let path = $(this).attr("data-tabs-path")
    $(".model__tab").removeClass("model__tab--active")
    $(this).addClass("model__tab--active")
    $(".model__content").removeClass("model__content--active")
    $(`.model__content[data-tabs-path="${path}"]`).addClass("model__content--active")
})

$(".stage__tab").click(function () {
    let path = $(this).attr("data-tabs-path")
    $(".stage__tab").removeClass("stage__tab--active")
    $(this).addClass("stage__tab--active")
    $(".stage__img").removeClass("stage__img--active")
    $(`.stage__img[data-tabs-path="${path}"]`).addClass("stage__img--active")
})

$(".qa-card__show").click(function () {
    $(this).parents(".qa-card").toggleClass("qa-card--active")
    $(this).siblings(".qa-card__hidden").slideToggle()
})

$(".filter__mob").click(function () {
    $(".filter").addClass("is-open")
    $("body").addClass("fixed-body")
})

$(".filter__close").click(function () {
    closeFilter()
})

$(".filter__bg").click(function () {
    closeFilter()
})

function closeFilter() {
    $(".filter").removeClass("is-open")
    $("body").removeClass("fixed-body")
}

