$(".my-select").niceSelect()

function mainSlider() {
    var swiper = new Swiper('.main .swiper-container', {
        slidesPerView: 1,
        spaceBetween: 0,
        speed: 2000,
        autoplay:{
            delay: 3000
        },
        effect: 'fade',
        fadeEffect: {
            crossFade: true
        },
        pagination: {
            el: '.main .swiper-pagination',
            type: 'bullets',
            clickable: true,
        },
    })
}

function projectSlider() {
    var swiper = new Swiper('.project .swiper-container', {
        slidesPerView: 1,
        spaceBetween: 0,
        pagination: {
            el: '.project .swiper-pagination',
            type: 'bullets',
            clickable: true,
        },
    })
}

function collectionSlider() {
    var swiper = new Swiper('.collection .swiper-container', {
        slidesPerView: "auto",
        spaceBetween: 40,
        navigation: {
            nextEl: '.collection .swiper-button-next',
            prevEl: '.collection .swiper-button-prev',
        },
    })
}

function aboutGallerySlider() {
    var swiper = new Swiper('.about-gallery .swiper-container', {
        slidesPerView: 3,
        spaceBetween: 50,
        pagination: {
            el: '.about-gallery .swiper-pagination',
            type: 'bullets',
            clickable: true,
        },
    })
}


mainSlider()
projectSlider()
collectionSlider()
aboutGallerySlider()

$(".model__tab").click(function () {
    let path = $(this).attr("data-tabs-path")
    $(".model__tab").removeClass("model__tab--active")
    $(this).addClass("model__tab--active")
    $(".model__content").removeClass("model__content--active")
    $(`.model__content[data-tabs-path="${path}"]`).addClass("model__content--active")
})

$(".stage__tab").click(function () {
    let path = $(this).attr("data-tabs-path")
    $(".stage__tab").removeClass("stage__tab--active")
    $(this).addClass("stage__tab--active")
    $(".stage__img").removeClass("stage__img--active")
    $(`.stage__img[data-tabs-path="${path}"]`).addClass("stage__img--active")
})

$(".qa-card__show").click(function () {
    $(this).parents(".qa-card").toggleClass("qa-card--active")
    $(this).siblings(".qa-card__hidden").slideToggle()
})

$(".header__burger").click(function() {
    $(".header").toggleClass("header--active")
    $("body").toggleClass("fixed-body")
})

